import React, { useState, useEffect } from 'react';

const PropertyList = () => {
  const [properties, setProperties] = useState([]);
  const [filter, setFilter] = useState(""); // Optional: Add filter input handling

  // Fetch the properties from the backend when the component mounts
  useEffect(() => {
    fetch('http://localhost:5000/properties') // Adjust URL if needed
      .then((response) => response.json())
      .then((data) => {
        setProperties(data); // Set the properties from the backend
      })
      .catch((error) => {
        console.error('Error fetching properties:', error);
      });
  }, []);

  // Optional: Filter the properties by location
  const filteredProperties = properties.filter((property) =>
    property.location.toLowerCase().includes(filter.toLowerCase())
  );

  return (
    <div className="property-list">
      {/* Optional: Add an input for filtering */}
      <input 
        type="text"
        placeholder="Filter by location" 
        value={filter}
        onChange={(e) => setFilter(e.target.value)}
      />
      {/* Display the filtered properties */}
      {filteredProperties.map((property) => (
        <div key={property.id} className="property-item">
          <h2>{property.name}</h2>
          <p>{property.location}</p>
          <p>${property.price}</p>
          <img
            src={property.image}  // Adjust the image field to match your backend response
            alt={property.name}
            style={{ width: "300px", height: "200px", objectFit: "cover" }} // Adjust size as needed
          />
        </div>
      ))}
    </div>
  );
};

export default PropertyList;
