import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';

const Signup = () => {
  // Validation schema using Yup
  const validationSchema = Yup.object({
    email: Yup.string().email('Invalid email format').required('Required'),
    username: Yup.string().required('Required'),
    password: Yup.string().min(6, 'Password must be at least 6 characters').required('Required'),
  });

  // Initial form values
  const initialValues = {
    email: '',
    username: '',
    password: '',
  };

  // Submit handler using a simple fetch
  const onSubmit = (values, { setSubmitting }) => {
    fetch('/signup', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(values), // Sending form data as JSON
    })
      .then(response => response.json())
      .then(data => {
        if (data.success) {
          console.log('Success:', data); // Handle success (e.g., navigate, show success message)
        } else {
          console.error('Error:', data); // Handle server errors
        }
      })
      .catch(error => {
        console.error('Fetch error:', error); // Handle fetch error
      })
      .finally(() => {
        setSubmitting(false); // End form submission state
      });
  };

  return (
    <div className="signup-container">
      <div className="signup-form">
        <h1>Prestige Properties</h1>

        <Formik
          initialValues={initialValues}
          validationSchema={validationSchema}
          onSubmit={onSubmit}
        >
          {({ isSubmitting }) => (
            <Form>
              <div>
                <Field
                  type="text"
                  name="email"
                  placeholder="Email"
                  className="input-field"
                />
                <ErrorMessage name="email" component="div" className="error" />
              </div>

              <div>
                <Field
                  type="text"
                  name="username"
                  placeholder="Username"
                  className="input-field"
                />
                <ErrorMessage name="username" component="div" className="error" />
              </div>

              <div>
                <Field
                  type="password"
                  name="password"
                  placeholder="Password"
                  className="input-field"
                />
                <ErrorMessage name="password" component="div" className="error" />
              </div>

              <button type="submit" className="signup-btn" disabled={isSubmitting}>
                Sign Up
              </button>
            </Form>
          )}
        </Formik>

        <div className="divider">
          <span>OR</span>
        </div>
        <button className="signup-fb">Sign up with Facebook</button>
      </div>

      <div className="login-link">
        <p>
          Have an account? <a href="/login">Log in</a>
        </p>
      </div>
    </div>
  );
};

export default Signup;
