import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import '../styles/Login.css'; // Ensure correct styles are applied

const Login = () => {
  // Validation schema using Yup
  const validationSchema = Yup.object({
    username: Yup.string().required('Username is required'),
    password: Yup.string().min(6, 'Password must be at least 6 characters').required('Password is required'),
  });

  // Initial form values
  const initialValues = {
    username: '',
    password: '',
  };

  // Submit handler using a simple fetch
  const onSubmit = (values, { setSubmitting }) => {
    console.log('Submitting form with values:', values); // Debugging log

    fetch('/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(values), // Sending form data as JSON
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        console.log('Response data:', data); // Debugging log
        if (data.success) {
          console.log('Login Successful:', data); // Handle success (e.g., redirect, show message)
        } else {
          console.error('Login Failed:', data); // Handle server errors
        }
      })
      .catch(error => {
        console.error('Fetch error:', error); // Handle fetch error
      })
      .finally(() => {
        setSubmitting(false); // End form submission state
      });
  };

  return (
    <div className="login-container">
      <div className="login-form">
        <h1>Prestige Properties</h1>

        <Formik
          initialValues={initialValues}
          validationSchema={validationSchema}
          onSubmit={onSubmit}
        >
          {({ isSubmitting }) => (
            <Form>
              <div>
                <Field
                  type="text"
                  name="username"
                  placeholder="Username"
                  className="input-field"
                />
                <ErrorMessage name="username" component="div" className="error" />
              </div>

              <div>
                <Field
                  type="password"
                  name="password"
                  placeholder="Password"
                  className="input-field"
                />
                <ErrorMessage name="password" component="div" className="error" />
              </div>

              <button type="submit" className="login-btn" disabled={isSubmitting}>
                {isSubmitting ? 'Logging in...' : 'Log In'}
              </button>
            </Form>
          )}
        </Formik>

        <div className="divider">
          <span>OR</span>
        </div>
        <button className="login-fb">Log in with Facebook</button>
        <p className="forgot-password">Forgot password?</p>
      </div>

      <div className="signup-link">
        <p>Don't have an account? <a href="/signup">Sign up</a></p>
      </div>
    </div>
  );
};

export default Login;
